// LanguageTranslator.js
import React, { useState } from 'react';
import './LanguageTranslator.css';

function LanguageTranslator() {
  const [text, setText] = useState('');
  const [translatedText, setTranslatedText] = useState('');
  const [language, setLanguage] = useState('en');

  // 字典映射 (為每種語言提供翻譯)
  const dictionaries = {
    en: {
      "你好": "Hello",
      "世界": "World",
      "早安": "Good morning",
      "再見": "Goodbye",
      "謝謝": "Thank you",
    },
    es: {
      "你好": "Hola",
      "世界": "Mundo",
      "早安": "Buenos días",
      "再見": "Adiós",
      "謝謝": "Gracias",
    },
    ja: {
      "你好": "こんにちは",
      "世界": "世界",
      "早安": "おはよう",
      "再見": "さようなら",
      "謝謝": "ありがとう",
    },
    fr: {
      "你好": "Bonjour",
      "世界": "Monde",
      "早安": "Bonjour",
      "再見": "Au revoir",
      "謝謝": "Merci",
    },
  };

  const handleTranslate = () => {
    // 根據選擇的語言選擇對應的字典
    const selectedDictionary = dictionaries[language];
    const words = text.split(" "); // 假設每個詞以空格分隔
    const translatedWords = words.map(word => selectedDictionary[word] || word); // 若字典中無對應詞則保留原詞
    setTranslatedText(translatedWords.join(" "));
  };

  return (
    <div className="translator-container">
      <h2>Language Translator</h2>
      <textarea
        placeholder="Enter text to translate..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      ></textarea>
      <div className="language-selector">
        <label htmlFor="language">Select language:</label>
        <select
          id="language"
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
        >
          <option value="en">English</option>
          <option value="es">Spanish</option>
          <option value="ja">Japanese</option>
          <option value="fr">French</option>
        </select>
      </div>
      <button onClick={handleTranslate}>Translate</button>
      <div className="output">{translatedText}</div>
    </div>
  );
}

export default LanguageTranslator;
